sp_angular_provider.script:
function ngAbeUnitVar() {

    function link(scope) {

        scope.units = ['px', 'em', 'vh', 'rem'];

        let re = /(?:([\d\.]*)(\D*))/g;

        function parseUnit(val) {

            let match = re.exec(val);

            scope.previous = {
                val: match[1],
                unit: match[2]
            };

            scope.current = {
                val: match[1],
                unit: match[2]
            };

            return val;
        }

        scope.addResetProcessor(parseUnit);
        scope.addLoadProcessor(parseUnit);

        scope.change = function () {
            scope.value = scope.current.val + scope.current.unit;
            scope.save(scope.vbl.sassVar, scope.value);
        };

        scope.init();
    }

    return {
        templateUrl: 'ng-abe-unit',
        require: '^^ngAbeVariable',
        link: link
    };
}
