sp_angular_provider.script:
function ngAbeUnitVar() {

    function NgAbeUnitVarLink (scope, elem, attr, ctrl) {
        ctrl[0].resetProcessor = function resetProcessor (val) {
            scope.stageValue = parseUnit(val);
            return val;
        };
    }

    function NgAbeUnitVarCtrl ($scope) {
        $scope.units = ['px', 'em', 'vh', 'rem'];
        $scope.stageValue = parseUnit($scope.value);

        $scope.change = function () {
            let val = $scope.stageValue.value + $scope.stageValue.unit;
            $scope.save(val);
        };
    }

    function parseUnit(val) {
        let re = /(?:([\d\.]*)(\D*))/g;
        let match = re.exec(val);

        return {
            value: match[1],
            unit: match[2]
        };
    }

    return {
        templateUrl: 'ng-abe-unit',
        require: ['^^ngAbeItem', '^^ngAbeVariable'],
        link: NgAbeUnitVarLink,
        controller: NgAbeUnitVarCtrl
    };
}
