sp_angular_provider.script:
function ngAbeUnitVar() {

    function link(scope) {

        let re = /(?:([\d\.]*)(\D*))/g;
        let match = re.exec(scope.abeValue);

        scope.units = ['px', 'em', 'vh', 'rem'];

        scope.previous = {
            val: match[1],
            unit: match[2]
        };

        scope.current = {
            val: match[1],
            unit: match[2]
        };

        scope.save = function () {
            if (scope.current.val !== scope.previous.val || scope.current.unit !== scope.previous.unit) {
                scope.abeChange({
                    sassVar: scope.abeVarData.sassVar,
                    value: scope.current.val + scope.current.unit
                });
            }
        };
    }

    return {
        templateUrl: 'ng-abe-unit',
        require: '^^ngAbeVariable',
        link: link
    };
}
