sp_angular_provider.script:
function ngAbeImageVar($http) {

    function ngAbeImageVarCtrl ($scope) {
        $scope.stageValue = $scope.value.substring(1, $scope.value.length - 1);
    }

    function ngAbeImageVarLink(scope, elem, attr, ctrl) {

        scope.trigger = trigger;
        scope.change = change;

        function change(files) {

            let ext = '.' + files[0].name.split('.').pop();
            let name = scope.item.sassVar + '-' + Date.now() + ext;

            $http({
                method: 'POST',
                url: '/api/now/table/db_image',
                data: {
                    'name': name
                }
            }).then(function (r) {

                let sysId = r.data.result.sys_id;

                $http({
                    method: 'POST',
                    url: '/api/now/attachment/file',
                    params: {
                        'table_name': 'db_image',
                        'file_name': 'image',
                        'table_sys_id': sysId
                    },
                    headers: {
                        'Content-Type': files[0].type
                    },
                    data: files[0]
                }).then(function () {
                    let val = "'" + name + "'";
                    ctrl.save(val);
                });

            });

        }

        function trigger() {
            elem.find('#uploadElem').click();
        }
    }

    return {
        templateUrl: 'ng-abe-image',
        require: ['^^ngAbeItem', '^^ngAbeVariable'],
        link: ngAbeImageVarLink,
        controller: ngAbeImageVarCtrl
    };
}
