sp_widget.client_script:
function abePreviewCtrl ($scope, ngAbeControl, $rootScope) {

	let c = this;

	$scope.setUrl = setUrl;

	$scope.suffix = '';
	$scope.search = '';

	setUrl(c.options.portal.urlSuffix, c.options.group.search);

	ngAbeControl.setPortalWatcher(function (portal) {

		let search = portal.defaultGroup ? portal.defaultGroup.search : '';

		setUrl(portal.url_suffix, search);
	});

	ngAbeControl.setGroupWatcher(function (group) {
		setSearch(group.search);
	});

	function setPortal (suffix) {
		if ($scope.suffix !== suffix) {
			$rootScope.$broadcast('abe.preview.loading');
			$scope.search = '';
			$scope.suffix = suffix;
			$scope.url = $scope.suffix + $scope.search;
		}
	}

	function setSearch (search) {
		if ($scope.search !== search) {
			$rootScope.$broadcast('abe.preview.loading');
			$scope.search = search;
			$scope.url = $scope.suffix + $scope.search;
		}
	}

	function setUrl (suffix, search) {

		console.log(suffix, search);

		if ($scope.suffix !== suffix || $scope.search !== search) {
			$rootScope.$broadcast('abe.preview.loading');
			$scope.suffix = suffix;
			$scope.search = search ? search : '';
			$scope.url = $scope.suffix + $scope.search;
		}
	}
}

sp_widget.css:
#abe-preview, #abe-preview > div, iframe {
  height: 100%;
  width: 100%;
}

iframe {
  pointer-events: none;
  border-radius: 3px;
  border: 0;
  box-shadow: 0 0 3px #ccc;
}

sp_widget.link:
function abePreviewLink (scope, elem) {

	let $rootScope = $injector.get('$rootScope');
	let $timeout = $injector.get('$timeout');
	let ngAbeHighlighter = $injector.get('ngAbeHighlighter');

	let iframe;
	let iframeElem;

	$timeout(function () {

		iframe = elem.find('iframe');

		iframeElem = elem.find('iframe')[0];

		iframeElem.onload = function () {
			scope.$apply(function () {
				$rootScope.$broadcast('abe.preview.loaded');
			});
		};
	}, 0);

	ngAbeHighlighter.init(function (selector) {
		let item = iframe.contents().find(selector)[0];

		let old = {
			border: item.style.border,
			borderRadius: item.style.borderRadius,
			boxShadow: item.style.boxShadow,
			padding: item.style.padding
		};

		item.style.border = "5px solid red";
		item.style.borderRadius = "10px";
		item.style.boxShadow = "0 0 10px red";
		item.style.padding = "2px";

		return function () {
			item.style.border = old.border;
			item.style.borderRadius = old.borderRadius;
			item.style.boxShadow = old.boxShadow;
			item.style.padding = old.padding;
		}
	});

	scope.$on('abe.preview.reload',function reloadPreview () {
		$rootScope.$broadcast('abe.preview.loading');
		iframeElem.contentWindow.location.reload(true);
		iframeElem.onload = function () {
			scope.$apply(function () {
				$rootScope.$broadcast('abe.preview.loaded');
			});
		};
	});

}
