sp_widget.client_script:
function AdvancedBrandingEditorCtrl (ngAbeControl, spUtil, $scope) {

	let c = this;


	function refreshData() {

	}

	function loadData() {

	}

	spUtil.recordWatch($scope, 'sp_portal', '', function (name, data) {
		c.server.refresh().then(function () {
			ngAbeControl.initPortals(c.data.portals);
			ngAbeControl.initGroups(c.data.groups);
		});
	});

	ngAbeControl.initPortals(c.data.portals);
	ngAbeControl.initGroups(c.data.groups);

	c.isInit = ngAbeControl.isInit;
}

