sp_widget.link:
function AdvancedBrandingEditorLink (scope) {

	var $rootScope = $injector.get('$rootScope');

	scope.loading = true;

	function showOverlay () {
		scope.loading = true;
	}

	function hideOverlay () {
		scope.loading = false;
	}

	$rootScope.$on('abe.save.start', showOverlay);
	$rootScope.$on('abe.preview.loading', showOverlay);
	$rootScope.$on('abe.preview.loaded', hideOverlay);
}

sp_widget.script:
(function() {

	var sysId = $sp.getParameter('sys_id');
	var groupId = $sp.getParameter('group_id');

	var ah = new AbeHelper();

	data.portals = ah.getPortals(sysId);
	data.groups = ah.getGroups(sysId, groupId);

})();
