sp_widget.link:
function (scope) {

	var $rootScope = $injector.get('$rootScope');
	var $timeout = $injector.get('$timeout');

	scope.loading = true;

	$rootScope.$on('abe.preview.loading',function () {
		scope.loading = true;

		$timeout(function () {
			scope.loading = false;
		}, 2000);
	});

	$rootScope.$on('abe.preview.loaded', function () {
		scope.loading = false;
	});

}

sp_widget.script:
(function() {

	data.sysId = $sp.getParameter('sys_id');
	data.groupId = $sp.getParameter('group_id');

	if (!data.sysId) {
		var portalGr = new GlideRecord('sp_portal');
		portalGr.addQuery('default', '1');
		portalGr.query();

		if (portalGr.next()) {
			data.sysId = portalGr.getUniqueValue();
		}
	}

	var gr = new GlideRecord('sp_portal');

	if (gr.get(data.sysId)) {

		data.portal = {
			title: gr.getDisplayValue('title'),
			urlSuffix: gr.getValue('url_suffix'),
			theme: gr.getValue('theme'),
			sysId: gr.getUniqueValue()
		};
	}

	var grpGr = new GlideRecord('x_si_abe_variable_group');

	if (data.groupId) {
		grpGr.get(data.groupId);
	} else {
		grpGr.addQuery('portal', data.portal.sysId);
		grpGr.setLimit(1);
		grpGr.orderBy('order');
		grpGr.query();
		grpGr.next();
	}

	data.group = {
		sysId: grpGr.getUniqueValue(),
		name: grpGr.getDisplayValue(),
		order: parseInt(grpGr.getValue('order')),
		search: grpGr.getValue('search')
	}

	data.opts = {
		portal: data.portal,
		group: data.group
	}

})();
