sp_widget.client_script:
function AbeEditorCtrl ($scope, ngAbeControl) {

	let c = this;

	c.changeGroup = changeGroup;
	c.getGroups = ngAbeControl.getGroups;

	function changeGroup (group) {
		ngAbeControl.setGroup(group);
	}

}

