sp_widget.client_script:
function AbeEditorCtrl ($scope, $rootScope, ngAbeControl) {

	let c = this;

	c.changeGroup = changeGroup;

	c.$onInit = function () {

		let properties = c.data.varString.match(/\$(.*?):(.*?);/g);

		c.vars = {};

		properties.forEach(function (cv) {
			let name = cv.slice(0, cv.indexOf(':'));
			c.vars[name] = cv.slice(cv.indexOf(':') + 1, cv.length).replace(';', '').trim();
		});
	};

	ngAbeControl.setPortalWatcher(function (portal) {
		c.server.get({
			sysId: portal.sysId
		}).then(function (r) {
			c.data = r.data;
			c.$onInit();
		});
	});

	function changeGroup (group) {
		c.data.groupId = group.sysId;
		ngAbeControl.setGroup(group);
	}

	$scope.$on('abe.variable.save', function (e, d) {
		c.server.get({
			action: 'saveSassVar',
			sassVar: d.vbl,
			value: d.val,
			sysId: c.data.sysId
		}).then(function (r) {
			c.data.variables = r.data.variables;
			c.data.groups = r.data.groups;
			c.data.varString = r.data.varString;
			c.$onInit();
			$rootScope.$broadcast('abe.preview.reload');
		});
	});
}

