sp_widget.script:
(function () {

    data.sysId = input ? input.sysId : options.portal.sysId;
    data.groupId = $sp.getParameter('group_id') ? $sp.getParameter('group_id') : false;

    var portalGr = new GlideRecord('sp_portal');
    portalGr.get(data.sysId);

    data.varString = portalGr.getValue('css_variables') || ' ';

    if (input && input.action === 'saveSassVar') {

        var rgx = new RegExp('(\\' + input.sassVar + ':)(.*?)(;)');

        if (data.varString.indexOf(input.sassVar) !== -1) {
            data.varString = data.varString.replace(rgx, '$1 ' + input.value + '$3');
        } else {
            data.varString = data.varString + '\n' + input.sassVar + ': ' + input.value + ';';
        }

        portalGr.setValue('css_variables', data.varString);
        portalGr.update();

    }

    var gr = new GlideRecord('x_si_abe_variable');
    gr.addQuery('variable_group.portal', portalGr.getUniqueValue());
    gr.orderBy('variable_group.order');
    gr.orderBy('variable_group');
    gr.query();

    data.variables = {};
    data.groups = [];

    while (gr.next()) {

        if (!data.variables[gr.getValue('variable_group')]) {

            var varGrp = gr.variable_group.getRefRecord();

            data.variables[gr.getValue('variable_group')] = {};

            data.groups.push({
                sysId: varGrp.getUniqueValue(),
                name: varGrp.getDisplayValue(),
                order: parseInt(varGrp.getValue('order')),
                search: varGrp.getValue('search')
            });

        }

        if (!data.variables[gr.getValue('variable_group')][gr.getValue('type')]) {
            data.variables[gr.getValue('variable_group')][gr.getValue('type')] = {
                label: gr.getDisplayValue('type'),
                vars: []
            };
        }

        data.variables[gr.getValue('variable_group')][gr.getValue('type')].vars.push({
            name: gr.getValue('name'),
            type: gr.getValue('type'),
            sassVar: gr.getValue('sass_variable'),
            description: gr.getValue('description'),
            defaultVal: gr.getValue('default'),
            cssSelector: gr.getValue('css_selector'),
            order: parseInt(gr.getValue('order'))
        });
    }

    if (!data.groupId && data.groups.length > 0) {
        data.groupId = data.groups[0].sysId;
    }

})();
